mapper.select:
SELECT ra.`id` FROM ${searchVo.schemaName}.`resource_appsystem` ra left join ${searchVo.schemaName}.`resource_appsystem_appmodule` raa on raa.resource_id = ra.id left join deploy_app_config_user dacu on ra.id = dacu.app_system_id and dacu.user_uuid = #{userUuid}
ra.`name` LIKE concat('%', #{searchVo.keyword}, '%') or raa.app_module_name LIKE concat('%', #{searchVo.keyword}, '%')
GROUP BY ra.id order by if(dacu.user_uuid = #{userUuid},1,0) desc,ra.name limit #{searchVo.startNum}, #{searchVo.pageSize}

select ra.id as appSystemId,ra.`name` as appSystemName, if(dacu.user_uuid is null,0,1) as isFavorite, if(dac.config is null,0,1) as isConfig, raa.app_module_id AS appModuleId, raa.app_module_name AS appModuleName from ${schemaName}.`resource_appsystem` ra left join ${schemaName}.`resource_appsystem_appmodule` raa on raa.resource_id = ra.id left join deploy_app_config_user dacu on ra.id = dacu.app_system_id and dacu.user_uuid = #{userUuid} left join deploy_app_config dac on ra.id = dac.app_system_id and dac.module_id = 0 and dac.env_id = 0 where ra.id in
#{id}
order by if(dacu.user_uuid = #{userUuid},1,0) desc, CONVERT(ra.name USING gbk) COLLATE gbk_chinese_ci ASC

sql.where:
app_system_id = #{appSystemId}

    and auth_uuid in

        #{authUuid}



    and `action` in

        #{action}



    and env_id in

        #{envId}

mapper.select:
( SELECT DISTINCT rse.env_id AS envId, rse.env_name AS envName FROM ${schemaName}.resource_appsystem_appmodule raa join ${schemaName}.resource_ipobject_appmodule ria on raa.app_module_id = ria.app_module_id join ${schemaName}.resource_appinstance ra on ria.resource_id = ra.id JOIN ${schemaName}.resource_softwareservice_env rse ON ra.id = rse.resource_id
raa.resource_id = #{appSystemId}
and ria.app_module_id in
#{id}
) UNION ( SELECT DISTINCT cc.id AS envId, cc.name AS envNaem FROM deploy_app_config_env dace JOIN cmdb_cientity cc ON cc.id = dace.env_id AND cc.ci_id = ( SELECT id FROM cmdb_ci where name = 'APPEnv' )
dace.app_system_id = #{appSystemId}
and dace.app_module_id in
#{id}
)

mapper.insert:
INSERT INTO `deploy_app_module_runner_group` (`app_system_id`, `module_id`, `runner_group_id`)
VALUES (#{appSystemId}, #{moduleId}, #{runnerGroupId})
ON DUPLICATE KEY UPDATE `runner_group_id` = #{runnerGroupId}

INSERT INTO deploy_app_env_auto_config
(`app_system_id`,`module_id`,`env_id`,`instance_id`,`key`,`value`,`lcd`,`lcu`)
VALUES

    #{appSystemId},#{moduleId},#{envId},#{instanceId},#{keyValue.key},#{keyValue.value},#{lcd},#{lcu}

ON DUPLICATE KEY UPDATE `key` = VALUES(`key`),`value` = VALUES(`value`),`lcd`= VALUES(`lcd`),`lcu` =
VALUES(`lcu`);

mapper.update:
UPDATE `deploy_app_config`
SET `config` = #{configStr},
    `lcu` = #{lcu},
    `lcd` = NOW(3)
WHERE `app_system_id` = #{appSystemId}
  and `module_id` = #{moduleId}
  and `env_id` = #{envId}

UPDATE `deploy_app_config_draft`
SET `config` = #{configStr},
    `lcu` = #{lcu},
    `lcd` = NOW(3)
WHERE `app_system_id` = #{appSystemId}
  and `module_id` = #{moduleId}
  and `env_id` = #{envId}

mapper.delete:
delete
from `deploy_app_config_authority`
where `app_system_id` = #{appSystemId}
and `env_id` = #{envId}
and `auth_uuid` = #{authUuid}

    and `lcd` != #{lcd}

delete
from deploy_app_env_auto_config
where
`app_system_id` = #{appSystemId}
and `module_id`=#{moduleId}
and `env_id` = #{envId}
and `instance_id` = #{instanceId}

    and `lcd` != #{lcd}
