mapper.select:
SELECT ra.`id` FROM ${searchVo.schemaName}.`resource_appsystem` ra left join ${searchVo.schemaName}.`resource_appsystem_appmodule` raa on raa.resource_id = ra.id left join deploy_app_config_user dacu on ra.id = dacu.app_system_id and dacu.user_uuid = #{userUuid}
ra.`name` LIKE concat('%', #{searchVo.keyword}, '%') or raa.app_module_name LIKE concat('%', #{searchVo.keyword}, '%')
GROUP BY ra.id order by if(dacu.user_uuid = #{userUuid},1,0) desc,ra.name limit #{searchVo.startNum}, #{searchVo.pageSize}

select ra.id as appSystemId,ra.`name` as appSystemName, if(dacu.user_uuid is null,0,1) as isFavorite, if(dac.is_config is null,0,1) as isConfig from ${schemaName}.`resource_appsystem` ra left join deploy_app_config_user dacu on ra.id = dacu.app_system_id and dacu.user_uuid = #{userUuid} left join deploy_app_config dac on ra.id = dac.app_system_id where ra.id in
#{id}
order by if(dacu.user_uuid = #{userUuid},1,0) desc,ra.name

sql.where:
app_system_id = #{appSystemId}

    and auth_uuid in

        #{authUuid}



    and `action` in

        #{action}



    and env_id in

        #{envId}

mapper.insert:
INSERT INTO `deploy_app_module_runner_group` (`app_system_id`, `module_id`, `runner_group_id`)
VALUES (#{appSystemId}, #{moduleId}, #{runnerGroupId})
ON DUPLICATE KEY UPDATE `runner_group_id` = #{runnerGroupId}

INSERT INTO deploy_app_env_auto_config
(`app_system_id`,`module_id`,`env_id`,`instance_id`,`key`,`value`,`lcd`,`lcu`)
VALUES

    #{appSystemId},#{moduleId},#{envId},#{instanceId},#{keyValue.key},#{keyValue.value},#{lcd},#{lcu}

ON DUPLICATE KEY UPDATE `key` = VALUES(`key`),`value` = VALUES(`value`),`lcd`= VALUES(`lcd`),`lcu` =
VALUES(`lcu`);

INSERT INTO `deploy_app_config` (`app_system_id`,`is_config`)
VALUES (#{appSystemId}, #{isConfig})
ON DUPLICATE KEY UPDATE `is_config` = #{isConfig}

mapper.delete:
delete
from `deploy_app_config_authority`
where `app_system_id` = #{appSystemId}
and `env_id` = #{envId}
and `auth_uuid` = #{authUuid}

    and `lcd` != #{lcd}

delete
from deploy_app_env_auto_config
where
`app_system_id` = #{appSystemId}
and `module_id`=#{moduleId}
and `env_id` = #{envId}
and `instance_id` = #{instanceId}

    and `lcd` != #{lcd}
