mapper.select:
select runner_map_id
from deploy_job
where app_system_id = #{appSystemId}
  and app_module_id = #{appModuleId}
order by job_id desc
limit 1

select id, app_system_id as appSystemId, app_module_id as appModuleId, env_id as envId, version, job_id as jobId, runner_map_id as runnerMapId from deploy_job
app_system_id = #{appSystemId}
and app_module_id = #{appModuleId}
