mapper.select:
select distinct djt.`id` from deploy_job_trigger djt left join deploy_job_trigger_app_module djtam on djt.id = djtam.trigger_id LEFT JOIN @{DATA_SCHEMA}.scence_appsystem_appmodule AS saa ON saa.id = djtam.app_system_id AND saa.app_module_id = djtam.app_module_id
AND djt.name LIKE concat('%',#{keyword},'%')
order by djt.`lcd` desc LIMIT #{startNum}, #{pageSize}
