mapper.update:
update deploy_version
set is_lock = #{isLock}
where id = #{id}

mapper.select:
select count(id)
from deploy_version
where id != #{id}
  and app_id = #{appId}
  and module_id = #{moduleId}
  and version = #{version}

select count(id)
from deploy_verison
where app_id = #{appId}
  and app_module_id = #{appModuleId}
